const { EventDispatcher } = require("event_dispatcher")


class CollectionController extends EventDispatcher {
    constructor(db) {
        super()
        this.c = db
    }

    insert(object) {
        return this.c.insert(object)
    }

    find(query) {
        return this.c.find(query)
    }

    update(query, fields) {
        return this.c.update(query, fields, {returnUpdatedDocs: true})
    }

    remove(query) {
        return this.c.remove(query, {})
    }
}

module.exports = {
    CollectionController: CollectionController
}