const { CollectionModel } = require("./collection.js")


class HistoryModel extends CollectionModel {
    constructor(db, trackCollection, collectionName) {
        super(collectionName)
        this.db = db
        this.tracks = trackCollection
    }

    append(trackObject, logData) {
        this.d.insert({
            trackId: trackObject._id,
            time_start: logData.time_start,
            time_end: logData.time_end,
            status: logData.status
        })   
    }
}

module.exports = {
    HistoryModel: HistoryModel
}