const { TrackSetModel } = require("./track_set.js")


class SessionModel extends TrackSetModel {
    constructor(db, collection, id) {
        super(db, collection, id)
    }

    async init() {
        let q = await this.collection.d.find({_id: this.l})
        if (q.length == 0) {
            await this.collection.d.insert({
                _id: "current_session",
                ordering:[],
                elements:{}
            })
        }
    }
}

module.exports = {
    SessionModel: SessionModel
}