const {Button} = require("ui/dom/button.js")

class DialogButton extends Button {
    constructor() {
        super()
        this.setStyle({
            padding: "6px 10px",
            textTransform: "uppercase",
            fontWeight: "bold",
            margin: "8px",
            fontSize: "10pt",
            border: "none",
            cursor: "pointer",
            height: "32px",
            borderRadius: "10px",
            outline: "none !important"
        })
    }
}


class ConfirmButton extends DialogButton {
    constructor() {
        super()
        this.setStyle({
            background: "rgb(30, 130, 30)",
        })
    }
}


class DismissButton extends DialogButton {
    constructor() {
        super()
        this.setStyle({
            background: "rgb(130, 30, 30)",
        })
    }
}


module.exports = {
    DialogButton: DialogButton,
    ConfirmButton: ConfirmButton,
    DismissButton: DismissButton
}
