
const { DOMElement } = require("ui/dom/domelement.js")


class Button extends DOMElement {
    constructor() {
        super("button")
        this.enabled = true
        this.domElement.addEventListener("click", (e) => {
            this.enabled && this.dispatch("click", this)
            e.stopPropagation()
        })
    }

    setText(text) {
        this.domElement.innerHTML = text
    }

    disable() {
        this.enabled = false
        this.domElement.classList.add("disabled")
    }

    enable() {
        this.enabled = true
        this.domElement.classList.remove("disabled")
    }
}

module.exports = {
    Button: Button
}