
const { DOMElement } = require("ui/dom/domelement.js")


class Checkbox extends DOMElement {
    constructor() {
        super("input")
        this.domElement.type = "checkbox"
        this.setStyle({
            overflow: "auto",
            height: "100%",
            maxHeight: "100%"
        })
        this.domElement.addEventListener("change", () => {
            this.dispatch("value-changed", this.domElement.checked)
        })
    }

    setChecked(value) {
        this.domElement.checked = value ? true : undefined
    }
}

module.exports = {
    Checkbox: Checkbox
}