const { DOMElement } = require("ui/dom/domelement.js")


class Image extends DOMElement {
    constructor() {
        super("img")
        this.setStyle({
            padding:"0px",
            margin: "0px"
        })
    }

    setSize(width, height) {
        this.domElement.width = width
        this.domElement.height = height
    }

    setImage(v) {
        this.domElement.src = v
    }
}

module.exports = {
    Image: Image
}