
const { DOMElement } = require("ui/dom/domelement.js")


class Label extends DOMElement {
    constructor(text) {
        super("div")
        this.text = text
        this.domElement.innerHTML = text
        // this.domElement.type = "checkbox"
        this.setStyle({
            // overflow: "auto",
            height: "100%",
            maxHeight: "100%"
        })
    }

    setText(text) {
        this.text = text
        this.domElement.innerHTML = text
    }

    getText() {
        return this.domElement.innerHTML
    }
}

module.exports = {
    Label: Label
}