const { DOMElement } = require("ui/dom/domelement.js")

class PropertySheet extends DOMElement {
    constructor() {
        super('table')
    }

    addItem(label, item) {
        item.setStyle({width: "100%", height: "100%"})
        let row = new DOMElement('tr')
        let labelCell = new DOMElement('td')
        labelCell.domElement.classList.add("tag-name")
        labelCell.domElement.innerHTML = label
        let itemCell = new DOMElement('td')
        itemCell.domElement.classList.add("tag-value")
        itemCell.appendChild(item)
        row.appendChildren([labelCell, itemCell])
        this.appendChild(row)
    }

    addSeparator() {
        this.menuPane.appendChild(new DOMElement('p'))
    }
}

module.exports = {
    PropertySheet: PropertySheet
}