const { DOMElement } = require("ui/dom/domelement.js")


class Slider extends DOMElement {
    constructor() {
        super("input")
        this.domElement.type = "range"
        this.domElement.oninput = () => {
            this.dispatch("value-changed", this.domElement.value)
        }
    }

    setBounds(min, max) {
        this.domElement.max = max
        this.domElement.min = min
    }

    setValue(v) {
        this.domElement.value = v
    }
}

module.exports = {
    Slider: Slider
}