const { DOMElement } = require("ui/dom/domelement.js")


class TextInput extends DOMElement {
    constructor() {
        super("input")
        this.domElement.type = "text"
        this.domElement.addEventListener("blur", console.log)
    }

    setText(text) {
        this.domElement.value = text
    }

    getText() {
        return this.domElement.value
    }
}

module.exports = {
    TextInput: TextInput
}