const { DOMElement } = require("ui/dom/domelement.js")


class StackLayout extends DOMElement {
    constructor() {
        super('div')
        this.items = {}
        this.currentItem = undefined
    }

    addItem(key, item) {
        if (!this.items[key]) {
            item.setStyle({
                width: "100%",
                height: "100%"
            })
            this.items[key] = item
            if (Object.keys(this.items).length == 1) {
                this.displayItem(key)
            }
        }
    }

    displayItem(key) {
        let item = this.items[key]
        if (!item) {
            return;
        }
        if (item != this.currentItem) {
            this.currentItem && this.removeChild(this.currentItem)
            this.appendChild(item)
            this.currentItem = item
        }
    }
}

module.exports = {
    StackLayout: StackLayout
}